EditText email_dest, subject, content;
 Button btn = findViewById(R.id.btn_send_email);
 email_dest = findViewById(R.id.ed_email_destination);
 email_dest = findViewById(R.id.ed_subject);
 email_dest = findViewById(R.id.ed_content);


btn.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View view) {
 Intent intent = new Intent(Intent.ACTION_SEND);
 intent.setType("message/rfc822");
 intent.putExtra(Intent.EXTRA_EMAIL, new String[] {String.valueOf(email_dest.getText())});
 intent.putExtra(Intent.EXTRA_SUBJECT, String.valueOf(subject.getText()));
 intent.putExtra(Intent.EXTRA_TEXT, String.valueOf(content.getText()));
 startActivity(Intent.createChooser(intent, "Send email"));
 }
 });

