    @Override\n
    protected void onCreate(Bundle savedInstanceState) {\n
        super.onCreate(savedInstanceState);\n
            btn = findViewById(R.id.btn_popup);\n\n

        btn.setOnClickListener(new View.OnClickListener() {\n
            @Override\n
            public void onClick(View v) {\n
                // Show the popup menu\n
                showPopupMenu(v);\n
            }\n
        });\n
        }\n
            private void showPopupMenu(View v) {\n
        PopupMenu popupMenu = new PopupMenu(getActivity(), v);\n
        popupMenu.getMenuInflater().inflate(R.menu.popup_menu, popupMenu.getMenu());\n\n

        // Set up the click listener for the popup menu items\n
        popupMenu.setOnMenuItemClickListener(new PopupMenu.OnMenuItemClickListener() {\n
            @Override\n
            public boolean onMenuItemClick(MenuItem item) {\n
                if (item.getItemId() == R.id.option1) {\n
                    Toast.makeText(getContext(), "You choosed the first option", Toast.LENGTH_SHORT).show();\n
                    return true;\n
                } else if (item.getItemId() == R.id.option2) {\n
                    Toast.makeText(getContext(), "You choosed the second option", Toast.LENGTH_SHORT).show();\n
                    return true;\n
                } else if (item.getItemId() == R.id.option3) {\n
                    Toast.makeText(getContext(), "You choosed the third option", Toast.LENGTH_SHORT).show();\n
                    return true;\n
                } else {\n
                    return false;\n
                }\n
            }\n
        });\n
        // Show the popup menu\n
        popupMenu.show();\n
    }\n
        }

